pragma solidity ^0.5.13;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/utils/ReentrancyGuard.sol";
import "./utils/interfaces/BToken.sol";

contract VotingMachine is ReentrancyGuard {
    using SafeMath for uint256;

    uint256 private totalStaked;
    mapping(address => uint256) private balances;
    BToken public balancerLiquidityToken;

    event Staked(address indexed user, uint256 amount);

    constructor(BToken _bToken) public {
        require(_bToken != BToken(0), "VotingMachine: BToken cannot be null");
        balancerLiquidityToken = _bToken;
    }

    function stake(uint256 amount) public nonReentrant {
        require(amount > 0, "VotingMachine: cannot stake 0");
        totalStaked = totalStaked.add(amount);
        balances[msg.sender] = balances[msg.sender].add(amount);
        balancerLiquidityToken.transferFrom(msg.sender, address(this), amount);
        emit Staked(msg.sender, amount);
    }

    function balanceOf(address account) public view returns (uint256) {
        return balances[account];
    }
}
